            **/
            function run(responseApi) {
                try {
                    let response = JSON.parse(responseApi);

                    let aux = "";
                    let count = 1;

                    response.produtos_disponiveis.forEach((produto) => {
                        aux = aux.concat(count, " - ", produto, "\n");
                        count++;
                    })

                    return aux;
                } catch(e) {
